import React, { useState, useContext } from 'react';
import { SimulationContext } from '../context/SimulationContext';
import BackButton from '../components/BackButton';

const scenarios = [
  "[OOÏùÄÌñâ] Í≥†Í∞ùÎãòÏùò Í≥ÑÏ¢åÍ∞Ä Ï†ïÏßÄÎêòÏóàÏäµÎãàÎã§. Î≥µÍµ¨Î•º ÏõêÌïòÏãúÎ©¥ ÎßÅÌÅ¨Î•º ÌÅ¥Î¶≠Ìï¥ Ï£ºÏÑ∏Ïöî: http://fakebank.com",
  "Î≥µÍµ¨Î•º ÏúÑÌï¥ Ïù∏Ï¶ùÎ≤àÌò∏Î•º ÏûÖÎ†•Ìï¥ Ï£ºÏÑ∏Ïöî.",
  "Ïù∏Ï¶ùÏù¥ ÌôïÏù∏ÎêòÏßÄ ÏïäÏúºÎ©¥ Í≥ÑÏ¢åÍ∞Ä ÌèêÏáÑÎê©ÎãàÎã§. ÏßÄÍ∏à Î∞îÎ°ú Ï≤òÎ¶¨ÌïòÏÑ∏Ïöî.",
];

const SimulationPage = () => {
  const { addToHistory } = useContext(SimulationContext);
  const [step, setStep] = useState(0);
  const [userResponse, setUserResponse] = useState('');
  const [chatLog, setChatLog] = useState([{ text: scenarios[0], isBot: true }]);
  const [finished, setFinished] = useState(false);

  const handleSubmit = () => {
    if (!userResponse.trim()) return;

    const feedback = evaluateResponse(userResponse);
    const newChat = [
      { text: userResponse, isBot: false },
      { text: feedback, isBot: true }
    ];

    setChatLog(prev => [...prev, ...newChat]);

    addToHistory({
      step,
      message: scenarios[step],
      userResponse,
      feedback
    });

    setUserResponse('');

    if (step < scenarios.length - 1) {
      setTimeout(() => {
        setStep(prev => prev + 1);
        setChatLog(prev => [...prev, { text: scenarios[step + 1], isBot: true }]);
      }, 800);
    } else {
      setFinished(true);
    }
  };

  const evaluateResponse = (input) => {
    const lower = input.toLowerCase();
    if (lower.includes("Î¨¥Ïãú") || lower.includes("ÏÇ≠Ï†ú") || lower.includes("Ïã†Í≥†")) {
      return "‚úÖ Ïò¨Î∞îÎ•∏ ÎåÄÏùëÏûÖÎãàÎã§!";
    } else if (lower.includes("ÌÅ¥Î¶≠") || lower.includes("ÏûÖÎ†•")) {
      return "‚ùå ÏûòÎ™ªÎêú ÎåÄÏùëÏûÖÎãàÎã§. ÌîºÏã±Ïóê Ï£ºÏùòÌïòÏÑ∏Ïöî.";
    }
    return "‚ÑπÔ∏è ÌîºÏã± Î©îÏãúÏßÄÎäî ÌÅ¥Î¶≠ÌïòÏßÄ ÏïäÍ≥† Î¨¥ÏãúÌïòÍ±∞ÎÇò Ïã†Í≥†ÌïòÎäî Í≤ÉÏù¥ Ï¢ãÏïÑÏöî.";
  };

  return (
    <div style={styles.page}>
      <div style={styles.wrapper}>
      
        <div style={styles.header}>
          <BackButton />
          <h2 style={styles.title}>ÏãúÎÆ¨Î†àÏù¥ÏÖò</h2>
        </div>

       
        <div style={styles.chatWrapper}>
          {chatLog.map((msg, idx) => (
            <div
              key={idx}
              style={msg.isBot ? styles.botBubble : styles.userBubble}
            >
              {msg.text}
            </div>
          ))}
        </div>

        
        {!finished ? (
          <div style={styles.inputWrapper}>
            <textarea
              value={userResponse}
              onChange={(e) => setUserResponse(e.target.value)}
              placeholder="ÎãµÎ≥ÄÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî..."
              rows={3}
              style={styles.textArea}
            />
            <button onClick={handleSubmit} style={styles.button}>
              ÎãµÎ≥ÄÌïòÍ∏∞
            </button>
          </div>
        ) : (
          <div style={styles.finishedMessage}>
            <p>üéâ ÏãúÎÆ¨Î†àÏù¥ÏÖòÏù¥ ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§.</p>
          </div>
        )}
      </div>
    </div>
  );
};

const styles = {
  page: {
    backgroundColor: '#f9f9f9',
    minHeight: '85vh',
    padding: '24px 16px 50px 16px',
    fontFamily: "'Segoe UI', sans-serif",
    display: 'flex',
    justifyContent: 'center',
   overflow: 'hidden',
  },
  wrapper: {
    maxWidth: '300px',
    width: '100%',
    display: 'flex',
    flexDirection: 'column',
  },
  title: {
    fontSize: '22px',
    fontWeight: 'bold',
    textAlign: 'center',
  },
  chatWrapper: {
    display: 'flex',
    flexDirection: 'column',
    gap: '12px',
    flexGrow: 1,
    overflowY: 'auto',
    backgroundColor: '#fff',
    borderRadius: '16px',
    padding: '16px',
    boxShadow: '0 4px 10px rgba(0,0,0,0.05)',
    minHeight: '100px',
  },
  inputWrapper: {
    marginTop: '20px',
    display: 'flex',
    flexDirection: 'column',
  },
  textArea: {
    borderRadius: '10px',
    padding: '12px',
    border: '1px solid #ccc',
    fontSize: '14px',
    resize: 'none',
    width: '90%',
    minHeight: '80px',
  },
  button: {
    marginTop: '10px',
    backgroundColor: '#2196f3',
    color: 'white',
    padding: '12px',
    border: 'none',
    borderRadius: '8px',
    fontWeight: 'bold',
    fontSize: '15px',
    cursor: 'pointer',
    transition: 'background-color 0.2s ease',
  },
  botBubble: {
    alignSelf: 'flex-start',
    backgroundColor: '#addaffff',
    color: '#333',
    padding: '10px 14px',
    borderRadius: '14px',
    maxWidth: '85%',
    wordBreak: 'keep-all',
  },
  userBubble: {
    alignSelf: 'flex-end',
    backgroundColor: '#2196f3',
    color: 'white',
    padding: '10px 14px',
    borderRadius: '14px',
    maxWidth: '85%',
    wordBreak: 'keep-all',
  },
  finishedMessage: {
    marginTop: '24px',
    textAlign: 'center',
    fontWeight: 'bold',
    color: '#555',
  },

  
  '@media (max-width: 480px)': {
    title: { fontSize: '18px' },
    chatWrapper: { minHeight: '300px', padding: '12px' },
    textArea: { fontSize: '13px', minHeight: '60px' },
    button: { fontSize: '13px', padding: '10px' },
    botBubble: { fontSize: '13px', padding: '8px 12px' },
    userBubble: { fontSize: '13px', padding: '8px 12px' },
  },
};

export default SimulationPage;
