import React from 'react';

const caseList = [
  { title: 'OOÏùÄÌñâ ÏÇ¨Ïπ≠ Î¨∏Ïûê', detail: 'ÎßÅÌÅ¨ ÌÅ¥Î¶≠ Ïãú Í∞úÏù∏Ï†ïÎ≥¥ ÌÉàÏ∑® ÏúÑÌóò' },
  { title: 'ÌÉùÎ∞∞ ÏÇ¨Ïπ≠ Ïä§ÎØ∏Ïã±', detail: 'Ïö¥ÏÜ°Ïû• Ï°∞Ìöå ÎßÅÌÅ¨Î°ú ÏïÖÏÑ± Ïï± ÏÑ§Ïπò Ïú†ÎèÑ' },
  { title: 'Í≥ÑÏ¢å Ïù∏Ï¶ù Ïú†ÎèÑ ÌîºÏã±', detail: 'Í≥µÍ≥µÍ∏∞Í¥Ä ÏÇ¨Ïπ≠ Î¨∏ÏûêÎ°ú Î≥¥ÏïàÏπ¥Îìú Î≤àÌò∏ ÏöîÍµ¨' },
];

const CasePage = () => {
  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h2 style={styles.headerTitle}>üí° ÏµúÏã† ÌîºÏã± ÏÇ¨Î°Ä</h2>
        <p style={styles.headerSubtitle}>ÏµúÍ∑º Î∞úÍ≤¨Îêú Ï£ºÏöî ÌîºÏã± Ïú†ÌòïÏùÑ ÌôïÏù∏ÌïòÏÑ∏Ïöî.</p>
      </div>

      {/* üîπ ÌîºÏã± ÏÇ¨Î°Ä Î™©Î°ù */}
      <div style={styles.caseList}>
        {caseList.map((item, idx) => (
          <div key={idx} style={styles.caseCard}>
            {/* ‚úÖ Ï†úÎ™©ÏùÑ Ïπ¥Îìú ÏÉÅÎã®Ïóê Ïò¨Î¶º */}
            <div style={styles.cardHeader}>
              <h4 style={styles.caseTitle}>‚ö†Ô∏è {item.title}</h4>
            </div>
            <p style={styles.caseDetail}>{item.detail}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

const styles = {
  container: {
    maxWidth: '900px',
    margin: '0 auto',
    padding: '40px 24px 40px 24px',
    backgroundColor: '#f9f9f9',
    boxSizing: 'border-box',
  },

  header: {
    textAlign: 'center',
    marginBottom: '0px',
  },

  headerTitle: {
    marginTop: '0px',
    fontSize: '24px',
    fontWeight: '700',
    color: '#000000ff',
  },

  headerSubtitle: {
    fontSize: '12px',
    color: '#777',
  },

  caseList: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(260px, 1fr))',
    gap: '12px',
  },

caseCard: {
  backgroundColor: '#fff',
  border: '1px solid #ddd',
  borderRadius: '8px',                  // ‚úÖ Î™®ÏÑúÎ¶¨ Îë•Í∑º Ï†ïÎèÑ Îçî Ï§ÑÏûÑ
  padding: '8px 10px',                  // ‚úÖ ÎÇ¥Î∂Ä Ïó¨Î∞± Ï∂ïÏÜå
  boxShadow: '0 1px 3px rgba(0,0,0,0.05)', // ‚úÖ Í∑∏Î¶ºÏûê ÏµúÏÜåÌôî
  transition: 'transform 0.2s ease, box-shadow 0.2s ease',
  cursor: 'pointer',
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'flex-start',
  minHeight: '30px',                    // ‚úÖ Ïπ¥Îìú ÎÜíÏù¥ Ï†ÑÏ≤¥Ï†ÅÏúºÎ°ú Ï∂ïÏÜå
},



  /* ‚úÖ Ï†úÎ™© ÏúÑÏπòÎ•º Ïπ¥Îìú ÏÉÅÎã®ÏúºÎ°ú ÎãπÍπÄ */
  cardHeader: {
    marginBottom: '6px',
  },

  caseTitle: {
    margin: '0',
    fontSize: '17px',
    fontWeight: '700',
    color: '#000000ff',
  },

  caseDetail: {
    marginTop: '4px', // ‚úÖ Í∞ÑÍ≤© Ï∂ïÏÜå
    fontSize: '14px',
    color: '#333',
    lineHeight: '1.4',
  },

  '@media (max-width: 768px)': {
    container: {
      padding: '20px 16px 40px 16px',
    },
    headerTitle: {
      fontSize: '20px',
    },
    caseCard: {
      padding: '16px',
    },
    caseTitle: {
      fontSize: '15px',
    },
    caseDetail: {
      fontSize: '13px',
    },
  },

  '@media (max-width: 480px)': {
    container: {
      padding: '16px 12px 50px 12px',
    },
    headerTitle: {
      fontSize: '18px',
    },
    headerSubtitle: {
      fontSize: '12px',
    },
    caseCard: {
      padding: '14px 12px',
      borderRadius: '10px',
    },
    caseTitle: {
      fontSize: '14px',
    },
    caseDetail: {
      fontSize: '12px',
    },
  },
};

export default CasePage;
