  const handleSubmit = () => {
    if (!userResponse.trim()) return;

    const feedback = evaluateResponse(userResponse);
    const newChat = [
      { text: userResponse, isBot: false },
      { text: feedback, isBot: true },
    ];

    setChatLog(prev => [...prev, ...newChat]);

