import React, { useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import CasePage from './CasePage';
import userIcon from '../assets/user.png';

const HomePage = () => {
  const navigate = useNavigate();
  const scrollRef = useRef(null);
  const animationRef = useRef(null);

  // ‚úÖ Î∂ÄÎìúÎü¨Ïö¥ ÏûêÎèô Ïä§ÌÅ¨Î°§
  useEffect(() => {
    const scrollContainer = scrollRef.current;
    if (!scrollContainer) return;

    let scrollAmount = 0;
    const scrollSpeed = 0.3;

    const animateScroll = () => {
      scrollAmount += scrollSpeed;
      if (scrollAmount >= scrollContainer.scrollWidth - scrollContainer.clientWidth) {
        scrollAmount = 0;
      }
      scrollContainer.scrollLeft = scrollAmount;
      animationRef.current = requestAnimationFrame(animateScroll);
    };

    animationRef.current = requestAnimationFrame(animateScroll);
    return () => cancelAnimationFrame(animationRef.current);
  }, []);

  return (
    <div style={styles.page}>
      {/* ÏÉÅÎã® ÌîÑÎ°úÌïÑ ÏòÅÏó≠ */}
      <div style={styles.header}>
        <button
          style={styles.profileButton}
          onClick={() => navigate('/mypage')}
          aria-label="ÎßàÏù¥ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô"
        >
          <img src={userIcon} alt="profile" style={styles.profileIcon} />
        </button>
      </div>

      {/* Î©îÏù∏ ÏΩòÌÖêÏ∏† */}
      <div style={styles.wrapper}>
        <div style={styles.todaySection}>
          <h2 style={styles.mainTitle}>
            üí° Ïò§ÎäòÏùò <span style={styles.highlight}>ÌîºÏã±</span> ÏÇ¨Î°Ä
          </h2>

          <div style={styles.noticeContainer} ref={scrollRef}>
            {[
              '‚ÄúOOÏùÄÌñâ ÏÇ¨Ïπ≠ Î¨∏Ïûê, ÌÅ¥Î¶≠ Ï£ºÏùò!‚Äù',
              '‚ÄúÌÉùÎ∞∞ ÏÇ¨Ïπ≠ ÌîºÏã±, 20ÎåÄ ÌîºÌï¥ Í∏âÏ¶ù‚Äù',
              '‚ÄúÍ≥ÑÏ¢å Ïù∏Ï¶ù Ïú†ÎèÑ ÌîºÏã± SMS Ïú†Ìñâ‚Äù',
              '‚ÄúÍµ≠ÏÑ∏Ï≤≠ ÌôòÍ∏â ÏÇ¨Ïπ≠ Î©îÏùº Ïú†Ìè¨ Ï§ë‚Äù',
            ].map((text, index) => (
              <div key={index} style={styles.noticeCard}>
                ‚ö†Ô∏è {text}
              </div>
            ))}
          </div>
        </div>

        <div style={styles.caseWrapper}>
          <CasePage />
        </div>
      </div>
    </div>
  );
};

// ‚úÖ Ïä§ÌÉÄÏùº Ï†ïÏùò
