  page: {
  position: 'fixed',
  top: 0,
  left: 0,
  width: '100vw',
  height: '100vh',    
  overflow: 'hidden', 
  backgroundColor: '#f9f9f9',
  fontFamily: "'Segoe UI', sans-serif",
  boxSizing: 'border-box',
