  header: {
    position: 'relative',
    display: 'flex',
    justifyContent: 'flex-end',
    alignItems: 'center',
    backgroundColor: '#f9f9f9',
    padding: '20px 20px 10px 20px',
  },

  profileButton: {
    background: 'none',
    border: 'none',
    cursor: 'pointer',
  },

  profileIcon: {
    width: '20px',
    height: '20x',
