    boxShadow: '0 2px 6px rgba(0,0,0,0.1)',
  },

  wrapper: {
    padding: '0 20px 40px 20px',
    maxWidth: '500px',
    margin: '0 auto',
  },

  mainTitle: {
    fontSize: '22px',
    fontWeight: '500',
    marginBottom: '5px',
    lineHeight: '1.5',
    textAlign: 'center',
  },


  // ‚úÖ Ïä§ÌÅ¨Î°§Î∞î ÏôÑÏ†Ñ Ï†úÍ±∞ + Î∂ÄÎìúÎü¨Ïö¥ ÏûêÎèô Ïù¥Îèô
