  },

  noticeCard: {
    display: 'inline-block',
    backgroundColor: '#addaffff',
    border: '1px solid #2196f3',
    borderRadius: '10px',
    padding: '12px 16px',
    fontSize: '14px',
    flexShrink: 0,
  },

  
};

