import React, { useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import CasePage from './CasePage';
import userIcon from '../assets/user.png';

const HomePage = () => {
  const navigate = useNavigate();
  const scrollRef = useRef(null);
  const animationRef = useRef(null);

  // ‚úÖ Î∂ÄÎìúÎü¨Ïö¥ ÏûêÎèô Ïä§ÌÅ¨Î°§
  useEffect(() => {
    const scrollContainer = scrollRef.current;
    if (!scrollContainer) return;

    let scrollAmount = 0;
    const scrollSpeed = 0.3;

    const animateScroll = () => {
      scrollAmount += scrollSpeed;
      if (scrollAmount >= scrollContainer.scrollWidth - scrollContainer.clientWidth) {
        scrollAmount = 0;
      }
      scrollContainer.scrollLeft = scrollAmount;
      animationRef.current = requestAnimationFrame(animateScroll);
    };

    animationRef.current = requestAnimationFrame(animateScroll);
    return () => cancelAnimationFrame(animationRef.current);
  }, []);

  return (
    <div style={styles.page}>
      {/* ÏÉÅÎã® ÌîÑÎ°úÌïÑ ÏòÅÏó≠ */}
      <div style={styles.header}>
        <button
          style={styles.profileButton}
          onClick={() => navigate('/mypage')}
          aria-label="ÎßàÏù¥ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô"
        >
          <img src={userIcon} alt="profile" style={styles.profileIcon} />
        </button>
      </div>

      {/* Î©îÏù∏ ÏΩòÌÖêÏ∏† */}
      <div style={styles.wrapper}>
        <div style={styles.todaySection}>
          <h2 style={styles.mainTitle}>
            üí° Ïò§ÎäòÏùò <span style={styles.highlight}>ÌîºÏã±</span> ÏÇ¨Î°Ä
          </h2>

          <div style={styles.noticeContainer} ref={scrollRef}>
            {[
              '‚ÄúOOÏùÄÌñâ ÏÇ¨Ïπ≠ Î¨∏Ïûê, ÌÅ¥Î¶≠ Ï£ºÏùò!‚Äù',
              '‚ÄúÌÉùÎ∞∞ ÏÇ¨Ïπ≠ ÌîºÏã±, 20ÎåÄ ÌîºÌï¥ Í∏âÏ¶ù‚Äù',
              '‚ÄúÍ≥ÑÏ¢å Ïù∏Ï¶ù Ïú†ÎèÑ ÌîºÏã± SMS Ïú†Ìñâ‚Äù',
              '‚ÄúÍµ≠ÏÑ∏Ï≤≠ ÌôòÍ∏â ÏÇ¨Ïπ≠ Î©îÏùº Ïú†Ìè¨ Ï§ë‚Äù',
            ].map((text, index) => (
              <div key={index} style={styles.noticeCard}>
                ‚ö†Ô∏è {text}
              </div>
            ))}
          </div>
        </div>

        <div style={styles.caseWrapper}>
          <CasePage />
        </div>
      </div>
    </div>
  );
};

// ‚úÖ Ïä§ÌÉÄÏùº Ï†ïÏùò
const styles = {
  page: {
  position: 'fixed',
  top: 0,
  left: 0,
  width: '100vw',
  height: '100vh',    
  overflow: 'hidden', 
  backgroundColor: '#f9f9f9',
  fontFamily: "'Segoe UI', sans-serif",
  boxSizing: 'border-box',
},

  header: {
    position: 'relative',
    display: 'flex',
    justifyContent: 'flex-end',
    alignItems: 'center',
    backgroundColor: '#f9f9f9',
    padding: '20px 20px 10px 20px',
  },

  profileButton: {
    background: 'none',
    border: 'none',
    cursor: 'pointer',
  },

  profileIcon: {
    width: '20px',
    height: '20x',
    borderRadius: '50%',
    objectFit: 'cover',
    boxShadow: '0 2px 6px rgba(0,0,0,0.1)',
  },

  wrapper: {
    padding: '0 20px 40px 20px',
    maxWidth: '500px',
    margin: '0 auto',
  },

  mainTitle: {
    fontSize: '22px',
    fontWeight: '500',
    marginBottom: '5px',
    lineHeight: '1.5',
    textAlign: 'center',
  },


  // ‚úÖ Ïä§ÌÅ¨Î°§Î∞î ÏôÑÏ†Ñ Ï†úÍ±∞ + Î∂ÄÎìúÎü¨Ïö¥ ÏûêÎèô Ïù¥Îèô
  noticeContainer: {
    overflowX: 'scroll',
    whiteSpace: 'nowrap',
    marginBottom: '0px',
    display: 'flex',
    gap: '10px',
    paddingBottom: '0px',
    scrollbarWidth: 'none', // Firefox
    msOverflowStyle: 'none', // IE/Edge
  },

  noticeCard: {
    display: 'inline-block',
    backgroundColor: '#addaffff',
    border: '1px solid #2196f3',
    borderRadius: '10px',
    padding: '12px 16px',
    fontSize: '14px',
    flexShrink: 0,
  },

  
};

// ‚úÖ Chrome / Safari Ï†ÑÏö© Ïä§ÌÅ¨Î°§Î∞î Ïà®ÍπÄ
if (typeof document !== 'undefined') {
  const styleSheet = document.createElement('style');
  styleSheet.innerHTML = `
    ::-webkit-scrollbar {
      display: none !important;
      width: 0;
      height: 0;
    }
  `;
  document.head.appendChild(styleSheet);
}

export default HomePage;
